#!/bin/vbash
# shellcheck disable=all

delete system host-name
set system host-name 'vg'
set system domain-name 'monosense.io'
set system ipv6 disable-forwarding
set system name-server '203.142.82.222'
set system name-server '203.142.84.222'
set system time-zone 'Asia/Jakarta'

set system sysctl parameter kernel.pty.max value '24000'

set system login user vyos authentication public-keys main-pc type 'ssh-rsa'
set system login user vyos authentication public-keys main-pc key ''
set system login user vyos authentication public-keys work-pc type 'ssh-rsa'
set system login user vyos authentication public-keys work-pc key ''
set system login user vyos authentication public-keys termux type 'ssh-rsa'
set system login user vyos authentication public-keys termux key ''
